def open_sesame
  ran = rand(KEY_WORDS.length.to_i)
  decrypt_key = KEY_WORDS["#{ran}".to_i]
end

def scrambler(message, format) 
  binding.pry
  arr = []
  message = message.split("") 
  message.each do |letter|
    arr << super_secrets_key_hash[format]["#{letter}"]
  end
  arr
end

def unscrambler(random_nums_array, format)
  z = super_secrets_key_hash[format].invert 
  random_nums_array.map do |number|
    z[number]
  end.join("")
end

def random_numbers
  numbers_array = []
  (ABC.length).times do 
    numbers_array << rand(100000)
  end
  numbers_array
end

def key_generator
  Hash[ABC.zip(random_numbers)]
end

def creates_hashes_for_decrypting
  @super_secrets_key_hash = {}
  i = 0
  KEY_WORDS.map do |word|
    shh_keys = key_generator
    @super_secrets_key_hash[KEY_WORDS[i]] = shh_keys
    i += 1
  end
end

