def lets_play
    loop do 
    puts "Welcome comrade! Would you like to SEND a message or READ a message? (SEND / READ / EXIT) " 
    response = gets.chomp
    if response == "SEND"
      print "What would you like to encrypt? " 
      response = gets.chomp
      format = open_sesame
      scrambled = scrambler(response, format)
      puts ""
      puts "Here is your key-gen '#{format}' and here is your code: #{scrambled}"
      puts ""
    elsif response == "READ" 
      print "Enter the message's key-gen: " 
      format = gets.chomp
      print "What would message would you like to decrypt? " 
      almost = gets.chomp[1..-2].split(", ")
      message = almost.map {|s| s.to_i }
      puts ""
      puts unscrambler(message, format)
      puts ""
    elsif response == "EXIT"
      puts ""
      puts ""
      puts "Dasvidaniya!"
      break 
    else
      puts ""
      puts ""
      puts "Comrade, you must type in one of the following: SEND / READ / EXIT "
      puts ""
      puts ""
    end
  end
end

lets_play

